SELECT panowie.IMIE,panowie.ROLA,panowie.id_pana,(sludzy.IMIE)AS IMIESLUGI,sludzy.id_szefa,(pracownicy.IMIE)AS IMIEPRACOWNIKA,pracownicy.SPECJALIZACJA,id_pracownika FROM panowie LEFT JOIN sludzy ON panowie.id_pana=sludzy.id_pana INNER JOIN pracownicy ON  pracownicy.id_szefa=sludzy.id_szefa WHERE ROLA='BANKIER' GROUP BY panowie.IMIE;
